.main-banner {
    display: inline-block;
    width: 100%;
    position: relative;
    img{
        object-fit: cover;
        width: 100%;
        max-height: 950px;
    }
    .banner-caption {
        width: 100%;
        display: block;
        margin: 0 auto;
        max-width: calc(100% - 30px);
        padding: 0;
        position: absolute;
        top: 50%;
        left: 0;
        right: 0;
        transform: translate(0%, -50%);
        z-index: 1;
        color: #fff;
        @include breakpoint("small") {
            position: absolute;
            top: 70%;
            left: 0;
            right: 0;
            transform: translate(0%, -70%);
            z-index: 1;
            color: #fff;
        }
        @include breakpoint("medium") {
            padding: 15px 0;
            top: 60%;
            transform: translate(0%, -60%);
        }
        @include breakpoint("large") {
            padding: 0 0 0 50px;
            max-width: calc(100% - 60px);
        }
        &.pad-left-sm {
            @include breakpoint("large") {
                padding-left: 50px;
            }
        }
        .caption-title {
            margin: 10px 0 10px;
            font-size: 24px;
            font-weight: 600;
            line-height: 1.3;
            color: #fff;
            @include breakpoint("small") {
                color: #fff;
                margin-top: 0;
                margin-bottom: 20px;
                font-size: 30px;
            }
            @include breakpoint("medium") {
                font-size: 48px;
                max-width: 480px;
                line-height: 1.1;
            }
            @include breakpoint("large") {
                font-size: 65px;
            }
            &.larger-title {
                font-size: 26px;
                @media (max-width: 374px) {
                    font-size: 24px;
                }
                @include breakpoint("small") {
                    font-size: 30px;
                }
                @include breakpoint("medium") {
                    font-size: 48px;
                    max-width: 800px;
                }
                @include breakpoint("large") {
                    font-size: 65px;
                }
            }
            &.full-caption-title {
                max-width: 100%;
            }
        }
        .caption-description {
            font-size: 16px;
            font-weight: 600;
            margin-bottom: 20px;
            @include breakpoint("small") {
                display: block;
            }
            @include breakpoint("medium") {
                font-size: 24px;
                margin-bottom: 40px;
            }
            @include breakpoint("large") {
                font-size: 35px;
            }
            &.larger-description {
                font-size: 16px;
                @include breakpoint("medium") {
                    font-size: 24px;
                }
                @include breakpoint("large") {
                    font-size: 35px;
                }
            }
            img {
                display: none;
            }
            img.cat-thumb-image{
                opacity: 0;
            }
            p.bg_banner {
                margin: 0;
            }
        }
    }
    .button {
        min-width: 175px;
        font-size: 16px;
        padding: 10px 20px;
        margin-bottom: 0;
        @include breakpoint("small") {
            font-size: 18px;
            padding: 15px 32px;
        }
    }
    .mobile-video {
        @include breakpoint("small") {
            display: none;
        }
        iframe {
            border-radius: 0;
        }
    }
    
    .desktop-video {
        display: none;
        @include breakpoint("small") {
            display: block;
        }
    }
}


