components/account/messages-form:
{{#if customer.inbox.orders}}
    <h2>{{lang 'forms.inbox.send_message'}}</h2>
    <form class="form" data-inbox-form action="{{urls.account.send_message}}" method="post">
        {{inject 'enterOrderNum' (lang 'forms.validate.account.inbox.order')}}
        {{inject 'enterSubject' (lang 'forms.validate.account.inbox.subject')}}
        {{inject 'enterMessage' (lang 'forms.validate.account.inbox.message')}}
        <div class="form-field">
            <label class="form-label" for="message_order_id">
                {{lang 'forms.inbox.order' }}
                <small>{{lang 'common.required' }}</small>
            </label>
            <select class="form-select" name="message_order_id" id="message_order_id">
                {{#each customer.inbox.orders}}
                    <option value="{{id}}">{{lang 'forms.inbox.order_display' id=id date=date total=amount.formatted}}</option>
                {{/each}}
            </select>
        </div>
        <div class="form-field" id="message_subject_field_id">
            <label class="form-label" for="message_subject">
                {{lang 'forms.inbox.subject' }}
                <small>{{lang 'common.required' }}</small>
            </label>
            <input aria-labelledby="message_subject_field_id" aria-live="polite" type="text" class="form-input" name="message_subject" id="message_subject">
        </div>
        <div class="form-field" id="message_content_field_id">
            <label class="form-label" for="message_content">
                {{lang 'forms.inbox.message' }}
                <small>{{lang 'common.required' }}</small>
            </label>

            <textarea aria-labelledby="message_content_field_id" aria-live="polite" class="form-input" name="message_content" id="message_content" rows="7" ></textarea>
        </div>
        <div class="form-actions">
            <input class="button button--primary" type="submit" value="{{lang 'forms.inbox.submit_value' }}">
            <input class="button" type="reset" value="{{lang 'forms.inbox.clear_value' }}">
        </div>
    </form>
{{else}}
    {{> components/common/alert/alert-info (lang 'forms.inbox.no_orders')}}
{{/if}}

components/common/channel-selector:
{{#if theme_settings.channels.length '>' 1}}
<ul class="navUser-section">
    <li class="navUser-item">
        <a href="#"
           class="navUser-action navUser-action--channelSelector has-dropdown"
           data-dropdown="channelSelection"
           aria-controls="channelSelection"
           aria-expanded="false"
        >
            {{#each theme_settings.channels}}
                {{#if is_active}}
                    {{lang 'common.channel' code=code}}
                {{/if}}
            {{/each}}
            <i class="icon" aria-hidden="true">
                <svg>
                    <use href="#icon-chevron-down"/>
                </svg>
            </i>
        </a>
        <ul class="dropdown-menu" id="channelSelection" data-dropdown-content aria-hidden="true" tabindex="-1">
            {{#each theme_settings.channels}}
                <li class="dropdown-menu-item">
                    <a href="{{{switch_url}}}" data-channel-code="{{{code}}}" data-warning="{{lang 'common.channel_switch_warning'}}">
                        {{#if is_active}}
                            <strong>{{name}}</strong>
                        {{else}}
                            {{name}}
                        {{/if}}
                    </a>
                </li>
            {{/each}}
        </ul>
    </li>
</ul>
{{/if}}

components/common/channel-selector-mobil:
{{#if theme_settings.channels.length '>' 1}}
<a href="#"
   class="navPages-action has-subMenu"
   data-collapsible="navPages-channel"
   aria-controls="navPages-channel"
   aria-expanded="false"
   aria-label="
    {{#each theme_settings.channels}}
        {{#if is_active}}
            {{lang 'common.channel' code=code}}
        {{/if}}
    {{/each}}
   "
>
    {{#each theme_settings.channels}}
        {{#if is_active}}
            {{lang 'common.channel' code=code}}
        {{/if}}
    {{/each}}
    <i class="icon navPages-action-moreIcon" aria-hidden="true">
        <svg>
            <use href="#icon-chevron-down"></use>
        </svg>
    </i>
</a>
<div class="navPage-subMenu" id="navPages-channel" aria-hidden="true" tabindex="-1">
    <ul class="navPage-subMenu-list">
        {{#each theme_settings.channels}}
        <li class="navPage-subMenu-item">
            <a href="{{{switch_url}}}"
               class="navPage-subMenu-action navPages-action"
               aria-label="{{name}}"
               data-channel-code="{{{code}}}"
               data-warning="{{lang 'common.channel_switch_warning'}}"
            >
                {{name}}
            </a>
        </li>
        {{/each}}
    </ul>
</div>
{{/if}}

components/common/navigation-list-altern:
{{#if children}}
    <a class="navPages-action navPages-action-depth-max has-subMenu is-root{{#if is_active}} activePage{{/if}}"
       href="{{url}}"
       data-collapsible="navPages-{{id}}"
       aria-label="{{name}}"
    >
        {{name}}
        <i class="icon navPages-action-moreIcon" aria-hidden="true">
            <svg><use href="#icon-chevron-down" /></svg>
        </i>
    </a>
    {{> components/common/navigation-dropdown}}
{{else}}
    <a class="navPages-action{{#if is_active}} activePage{{/if}}"
       href="{{url}}"
       aria-label="{{name}}"
    >
        {{name}}
    </a>
{{/if}}

pages/account/inbox:
{{#partial "page"}}

{{> components/common/breadcrumbs breadcrumbs=breadcrumbs}}
<h1 class="page-heading">{{lang 'account.messages.heading' }}</h1>
{{> components/account/navigation account_page='messages'}}

<main class="account account--fixed">
    <div class="account-body">
        <section class="account-content">
            {{#if forms.inbox.error}}
                {{> components/common/alert/alert-error forms.inbox.error}}
            {{/if}}
            {{#if forms.inbox.success}}
                {{> components/common/alert/alert-success forms.inbox.success}}
            {{/if}}
            {{> components/account/messages-list messages=customer.inbox.messages}}
            {{> components/account/messages-form}}
        </section>
    </div>
</main>

{{/partial}}
{{> layout/base}}
